import Ticker from '@statetree/ticker';

/*
* Wrapper for function to hold reference to the context*/
export default class Entry {
    constructor(func, context = null){
        this.context = context;
        this.func = func;
    }
}

// Method available only on Entry instance not in Class
Entry.prototype.dispose = function (){
    if(this.context instanceof Ticker){
        this.context.dispose();
    }
    this.context = null;
    this.func = null;
}

import Ticker from '@statetree/ticker';
import {executeInSyncOrAsync, executeEntries, disposeAndRemoveEntry,executeAsync} from './helpers';
import Entry from './entry';

function getTickerEntry(func, context, priority, errorCallback, funcsInst){
	const ticker = new Ticker(func, context, priority);
	function doneNotifier(){
		funcsInst.remainingEntries = funcsInst.remainingEntries - 1;
	};
	ticker.onDone(doneNotifier).onError(errorCallback);
	return new Entry(ticker.executeInCycle, ticker);
}

export default class Functions {

	constructor(connector = null) {
		this._entries = [];
		this._tickerEntries = []; // ticker entries
		this.remainingEntries = 0;
		this.connector = connector; // connector is responsible for sequencing next function set
		this._enableConnector = true;
	}
	/**
	 * Based on return value of predicate, this function decides whether to execute the method immediately or in frame cycle
	 *
	 * @param {function} apiFunc
	 * @param {function} callback Api func execution may be sync or Async, if its sync we cant return notifier as user can register doneCallback after API invocation
	 * @param {function} errorCallback
	 * @return {void}
	 */
	executeWhenIdle(apiFunc, callback, errorCallback){
		const predicate = ()=>{
			if(this.remainingEntries < 0){
				throw new Error("There can't be negative entries")
			}
			return this.remainingEntries === 0;
		};
		if(!errorCallback){
			errorCallback = (error)=>console.log(error);
		}
		executeInSyncOrAsync(predicate, apiFunc, callback, errorCallback);
	};

	/**
	 * Based on return value of predicate, this function decides whether to execute the method in immediate frame cycle or following cycle
	 *
	 * @param {function} apiFunc
	 * @return {object} notifier
	 */
	executeAsyncWhenIdle(apiFunc){
		const predicate = ()=>{
			if(this.remainingEntries < 0){
				throw new Error("There can't be negative entries")
			}
			return this.remainingEntries === 0;
		};
		return executeAsync(predicate, apiFunc)
	}

	/**
	 * execute all the entries
	 *
	 * @param {function} callback
	 * @return {void}
	 */
	trigger(callback = null){
		const that = this;
		const _trigger = ()=>{
			const {_entries, _tickerEntries} = that;
			const shouldTrigger = that.shouldExecuteFunctions();
			if(shouldTrigger){
				that.functionsWillExecute();
				(_entries.length > 0) && executeEntries(_entries);
				if (_tickerEntries.length > 0) {
					that.remainingEntries = executeEntries( _tickerEntries, true)
				};
			}
		};

		const entriesExecutionCompletedNotifier = ()=>{
			if(that.remainingEntries === 0){
				callback && callback()
				that.functionsDidExecute();
			} else {
				that.executeWhenIdle(entriesExecutionCompletedNotifier);
			}
		};

		this.executeWhenIdle( _trigger, entriesExecutionCompletedNotifier);
	};

	/**
	 * Add the function to entries
	 * if trigger is in progress, adds once its completed
	 *
	 * @param {function} func
	 * @param {object} context
	 * @param {boolean} executeInCycle
	 * @param {number} priority
	 * @param {function} callback
	 * @param {function} errorCallback
	 * @return {void}
	 */
	addFunction(func, context = null, executeInCycle = false, priority = 0, callback = null, errorCallback = null){
		const _addFunction = () => {
			let entry = new Entry(func, context, priority);
			if (executeInCycle){
				entry = getTickerEntry(func, context, priority, errorCallback, this);
				this._tickerEntries.push(entry);
			} else {
				entry = new Entry(func, context);
				this._entries.push(entry);
			}
			callback && callback(entry)
		};
		this.executeWhenIdle(_addFunction);
	};

	/**
	 * remove the function from entries
	 * if trigger is in progress, removes once its completed
	 *
	 * @param {function} func
	 * @param {object} context
	 * @param {function} callback
	 * @return {void}
	 */
	removeFunction(func, context = null, callback = null){
		const _removeFunction = ()=> {
			const {_tickerEntries, _entries} = this;
			disposeAndRemoveEntry(func,context,_entries);
			disposeAndRemoveEntry(func,context,_tickerEntries , true);
			callback && callback();
		}

		this.executeWhenIdle(_removeFunction);
	};

	/**
	 * Should be implemented by the class which inherits it,
	 * will be called before executing entries
	 */
	functionsWillExecute(){

	};

	/**
	 * Should be implemented by the class which inherits it,
	 * Should be a predicate function which decides whether to execute the entries.
	 * @return {boolean}
	 */
	shouldExecuteFunctions(){
		return true;
	};

	/**
	 * Should be extended by calling super.functionsDidExecute() by the class which inherits it,
	 * If connector is enabled , this will trigger the function that was referenced to connector
	 */
	functionsDidExecute(){
		this._enableConnector && this.connector && this.connector();
	};

	/**
	 * sets the connector function which is the binder for next sequence of actions
	 * if trigger is in progress, sets once its completed
	 *
	 * @param {function} connector
	 * @return {void}
	 */
	setConnector(connector){
		const _setConnector = () => {
			this.connector = connector;
		}
		this.executeWhenIdle(_setConnector);

	}

	/**
	 * removes the connector function which is the binder for next sequence of actions
	 * if trigger is in progress, removes once its completed
	 *
	 * @return {void}
	 */
	removeConnector(){
		const _removeConnector = () => {
			this.connector = null;
		};
		this.executeWhenIdle(_removeConnector);
	}

	/**
	 * enables the connector function which is the binder for next sequence of actions
	 * if trigger is in progress, enables once its completed
	 *
	 * @return {void}
	 */
	linkConnector(){
		const _linkConnector = () => {
			this._enableConnector = true;
		};
		this.executeWhenIdle(_linkConnector);
	}

	/**
	 * disables the connector function which is the binder for next sequence of actions
	 * if trigger is in progress, disables once its completed
	 *
	 * @return {void}
	 */
	unLinkConnector(){
		const _unLinkConnector = () => {
			this._enableConnector = false;
		};
		this.executeWhenIdle(_unLinkConnector);
	}
}

import Ticker from "@statetree/ticker";

/**
 * Based on return value of predicate, this function decides whether to execute the method immediately or in frame cycle
 *
 * @param {function} predicate
 * @param {function} apiFunc
 * @param {function} callback Api func execution may be sync or Async, if its sync we cant return notifier as user can register doneCallback after API invocation
 * @param {function} errorCallback
 * @return {void}
 */
export function executeInSyncOrAsync(predicate, apiFunc, callback, errorCallback){
	// Important to create inside closure to execute in Ticker
	const _executeLater = ()=>{
		if(predicate()){ // execute API calls Only after all user added functions are executed
			const returnValue = apiFunc();
			callback && callback(returnValue);
		} else {
			let ticker = new Ticker(_executeLater, null, 2);
			errorCallback && ticker.onError(errorCallback);
			ticker.executeInCycle()
		}
	};
	_executeLater();
};

export function executeAsync(predicate, apiFunc){
	// Important to create inside closure to execute in Ticker
	const _executeLater = ()=>{
		let ticker;
		if(predicate()){ // execute API calls Only after all user added functions are executed
			ticker = new Ticker(apiFunc, null, 2);
		} else {
			ticker = new Ticker(_executeLater, null, 2);
		}
		return ticker.executeInCycle()
	};
	return _executeLater();
};

/**
 * Executes all the stored functions , if callLater enabled, executes them in frame cycle
 *
 * @param {array} entries of  which contains the function and its context.
 * @param {boolean} callLater indicates execution needs to happen in frame cycle
 * @return {number} return count of entries that are added to execute in loop later
 */
export function executeEntries(entries, callLater = false){
	const disposedEntriesIndex = [];
	let delayedEntriesCount = 0;
	entries.forEach(function(entry, index){
		if (entry.func) {
			callLater && (delayedEntriesCount = delayedEntriesCount + 1);
			entry.func.apply(entry.context || entry.func['this']);
		} else {
			disposedEntriesIndex.push(index);
		}
	});
	clearDisposedEntries(disposedEntriesIndex,entries);
	return delayedEntriesCount;
}

function clearDisposedEntries(indices, entries){
	if(typeof indices === 'number'){
		entries.splice(indices,1);
	} else {
		indices.forEach(function(entryIndex){
			entries.splice(entryIndex,1);
		})
	}
}

function getEntry(list, index, isTicker){
	if(isTicker){
		const tickerEntry =  list[index];
		return tickerEntry.context;
	} else {
		return list[index];
	}
}


export function disposeAndRemoveEntry(func, context, list , isTicker){
	for(let i = 0; i < list.length; i++){
		let entry = getEntry(list,i,isTicker);
		if(entry && entry.func && entry.func === func && entry.context === context){
			list[i].dispose();
			clearDisposedEntries(i, list);
			return;
		}
	}
};

import Functions from './functions';
import {executeInSyncOrAsync, executeAsync} from './helpers';

export {
	Functions as default,
	executeInSyncOrAsync,
	executeAsync
}

import {expect} from 'chai';
import Functions from './../src/lib/functions'

let functions, func1, func2, func3, func4, connector;
let executionCount = 0;
let connectorExecuted = 0;
describe ('API', ()=>{
	beforeEach(()=>{
		func1 = function(){executionCount++};
		func2 = function(){executionCount++};
		func3 = function(){executionCount++};
		func4 = function(){executionCount++};
		connector = function(){ connectorExecuted = 1;};
		functions = new Functions(connector);
	});

	afterEach(()=>{
		functions = null;
		executionCount = 0;
		connectorExecuted = 0;
	});

	describe('addFunction', ()=>{
		it('Should add function to entries array by default', ()=>{
			functions.addFunction(func1);
			expect(functions._entries.length).equal(1);
			expect(functions._tickerEntries.length).equal(0);
			functions.addFunction(func2);
			expect(functions._entries.length).equal(2);
			expect(functions._tickerEntries.length).equal(0);
		});
		it('Should add function to tickerEntries array if executeInCycle is set to true', ()=>{
			functions.addFunction(func3, null, true);
			expect(functions._tickerEntries.length).equal(1);
			expect(functions._entries.length).equal(0);
			functions.addFunction(func4,  null, true);
			expect(functions._tickerEntries.length).equal(2);
			expect(functions._entries.length).equal(0);
		});
		it('should not execute the function', ()=>{
			functions.addFunction(func1);
			expect(executionCount).equal(0);
		});
		it('if function execution in progress, should add after completion', (done)=>{
			functions.addFunction(func1, null, true);
			functions.addFunction(func2, null, true);
			functions.trigger();
			functions.addFunction(func3);
			functions.addFunction(func4, null, true);
			expect(functions.remainingEntries).equal(2);
			expect(functions._tickerEntries.length).equal(2);
			expect(functions._entries.length).equal(0);
			setTimeout(()=>{
				expect(functions.remainingEntries).equal(0);
				expect(functions._tickerEntries.length).equal(3);
				expect(functions._entries.length).equal(1);
				done();
			},0);
		});
		it('callback should be called after adding', (done)=>{
			expect(functions._entries.length).equal(0);
			functions.addFunction(func1, null, false, 0 , ()=>{
				expect(functions._entries.length).equal(1);
			});

			expect(functions._tickerEntries.length).equal(0);
			functions.addFunction(func2, null, true, 0 , ()=>{
				expect(functions._tickerEntries.length).equal(1);
				done();
			});

		});
	})
	describe('removeFunction', ()=>{
		it('Should remove function to entries array by default', ()=>{
			functions.addFunction(func1);
			expect(functions._entries.length).equal(1);
			functions.addFunction(func2);
			expect(functions._entries.length).equal(2);
			functions.removeFunction(func1);
			expect(functions._entries.length).equal(1);
		});
		it('Should add function to tickerEntries array if executeLaterInNextTick is set to true', ()=>{
			functions.addFunction(func3, null, true);
			expect(functions._tickerEntries.length).equal(1);
			functions.addFunction(func4,  null, true);
			expect(functions._tickerEntries.length).equal(2);
			functions.removeFunction(func3);
			expect(functions._tickerEntries.length).equal(1);
		});
		it('if function execution in progress, should remove after completion', (done)=>{
			functions.addFunction(func1, null, true);
			functions.addFunction(func2, null, true);
			functions.trigger();
			functions.removeFunction(func1);
			expect(functions.remainingEntries).equal(2);
			expect(functions._tickerEntries.length).equal(2);
			setTimeout(()=>{
				expect(functions.remainingEntries).equal(0);
				expect(functions._tickerEntries.length).equal(1);
				done();
			},0);
		});
		it('callback should be called after removing', (done)=>{
			functions.addFunction(func1);
			functions.addFunction(func2,null, true);
			expect(functions._entries.length).equal(1);
			expect(functions._tickerEntries.length).equal(1);
			functions.removeFunction(func1, null, ()=>{
				expect(functions._entries.length).equal(0);
			})
			functions.removeFunction(func2, null, ()=>{
				expect(functions._tickerEntries.length).equal(0);
				done()
			})

		});
	})
	describe('trigger', ()=>{
		it('Should execute all added functions ', ()=>{
			functions.addFunction(func1);
			functions.addFunction(func2);
			functions.trigger();
			expect(executionCount).equal(2);
			executionCount = 0;
			functions.removeFunction(func2);
			functions.trigger();
			expect(executionCount).equal(1);
		});
		it('Should execute callback after all functions are executed', (done)=>{
			functions.addFunction(func1, null, true);
			functions.addFunction(func2);
			functions.trigger(()=>{
				expect(executionCount).equal(2);
				done();
			});
			expect(executionCount).equal(1);
		});
		it('Should execute connector after callback ', (done)=>{
			functions.addFunction(func1, null, true);
			functions.addFunction(func2);
			functions.trigger(()=>{
				expect(connectorExecuted).equal(0);
				expect(executionCount).equal(2);
				setTimeout(()=>{
					expect(connectorExecuted).equal(1);
					done();
				},0);
			});
			expect(executionCount).equal(1);
		});

	});
	describe('setConnector', ()=>{
		it('Should add new connector ', ()=>{
			functions.removeConnector(connector);
			expect(functions.connector).equal(null);
			functions.setConnector(connector);
			expect(functions.connector).equal(connector);
		});
		it('if we set connector in the middle of execution, connector should wait till the current trigger get completed', (done)=>{
			functions.removeConnector(connector);
			expect(functions.connector).equal(null);
			functions.addFunction(func1, null, true);
			functions.addFunction(func2, null, true);
			functions.trigger(()=>{
				expect(executionCount).equal(2);
				expect(functions.connector).equal(null);
			});
			functions.setConnector(connector);
			expect(functions.connector).equal(null);
			functions.trigger(()=>{
				expect(connectorExecuted).equal(0);
				expect(functions.connector).equal(connector);
				expect(executionCount).equal(4);
				setTimeout(()=>{
					expect(connectorExecuted).equal(1);
					done();
				},0);
			});
		});
	});
	describe('removeConnector', ()=>{
		it('Should remove the connector ', ()=>{
			functions.removeConnector(connector);
			expect(functions.connector).equal(null);
		});
		it('if we set connector in the middle of execution, connector should wait till the current trigger get completed', (done)=>{
			functions.addFunction(func1, null, true);
			functions.addFunction(func2, null, true);
			functions.trigger(()=>{
				expect(connectorExecuted).equal(0);
				expect(executionCount).equal(2);
				setTimeout(()=>{
					expect(connectorExecuted).equal(1);
					connectorExecuted = 0;
				},0);
			});
			functions.removeConnector(connector);
			expect(functions.connector).equal(connector);
			functions.trigger(()=>{
				expect(functions.connector).equal(null);
				expect(connectorExecuted).equal(0);
				expect(executionCount).equal(4);
				setTimeout(()=>{
					expect(connectorExecuted).equal(0);
					done();
				},0);
			});
		});
	});
	describe('linkConnector', ()=>{
		it('Should execute connector after all functions are executed ', (done)=>{
			functions.linkConnector(connector);
			functions.addFunction(func1);
			functions.addFunction(func2);
			functions.trigger(()=>{
				expect(connectorExecuted).equal(0);
				expect(executionCount).equal(2);
				setTimeout(()=>{
					expect(connectorExecuted).equal(1);
					done();
				},0);
			});
		});
		it('if we add connector in the middle of execution, connector should not execute for the current trigger and execute for next trigger', (done)=>{
			functions.unLinkConnector(connector);
			functions.addFunction(func1, null, true);
			functions.addFunction(func2, null, true);
			functions.trigger(()=>{
				expect(connectorExecuted).equal(0);
				expect(executionCount).equal(2);
				setTimeout(()=>{
					expect(connectorExecuted).equal(0);
				},0);
			});
			functions.linkConnector(connector);
			functions.trigger(()=>{
				expect(connectorExecuted).equal(0);
				expect(executionCount).equal(4);
				setTimeout(()=>{
					expect(connectorExecuted).equal(1);
					done();
				},0);
			});
		});
	})
	describe('unLinkConnector', ()=>{
		it('Should not execute connector if removed ', (done)=>{
			functions.unLinkConnector(connector);
			functions.addFunction(func1);
			functions.addFunction(func2);
			functions.trigger(()=>{
				expect(connectorExecuted).equal(0);
				expect(executionCount).equal(2);
				setTimeout(()=>{
					expect(connectorExecuted).equal(0);
					done();
				},0);
			});
		});
		it('if we unlink connector in the middle of execution, connector should still execute for the current trigger and not for next trigger', (done)=>{
			functions.addFunction(func1, null, true);
			functions.addFunction(func2, null, true);
			functions.trigger(()=>{
				expect(connectorExecuted).equal(0);
				expect(executionCount).equal(2);
				setTimeout(()=>{
					expect(connectorExecuted).equal(1);
					connectorExecuted = 0;
				},0);
			});
			functions.unLinkConnector(connector);
			functions.trigger(()=>{
				expect(connectorExecuted).equal(0);
				expect(executionCount).equal(4);
				setTimeout(()=>{
					expect(connectorExecuted).equal(0);
					done();
				},0);
			});
		});
	})
});
